// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract CharityManager is Ownable, ReentrancyGuard {
    struct Charity {
        string name;
        string description;
        address wallet;
        uint256 totalDonations;
        bool isActive;
    }

    mapping(uint256 => Charity) public charities;
    mapping(address => uint256[]) public userDonations;
    uint256 public charityCount;

    event CharityCreated(uint256 indexed charityId, string name, address wallet);
    event DonationReceived(uint256 indexed charityId, address indexed donor, uint256 amount);
    event CharityStatusChanged(uint256 indexed charityId, bool isActive);

    constructor() {
        charityCount = 0;
    }

    function createCharity(string memory name, string memory description, address wallet) public onlyOwner {
        charities[charityCount] = Charity(name, description, wallet, 0, true);
        emit CharityCreated(charityCount, name, wallet);
        charityCount++;
    }

    function donate(uint256 charityId) public payable nonReentrant {
        require(charityId < charityCount, "Invalid charity ID");
        Charity storage charity = charities[charityId];
        require(charity.isActive, "Charity is not active");

        charity.totalDonations += msg.value;
        userDonations[msg.sender].push(charityId);
        emit DonationReceived(charityId, msg.sender, msg.value);
        payable(charity.wallet).transfer(msg.value);
    }

    function setCharityStatus(uint256 charityId, bool status) public onlyOwner {
        require(charityId < charityCount, "Invalid charity ID");
        charities[charityId].isActive = status;
        emit CharityStatusChanged(charityId, status);
    }
}

